      totalsoFar: !currentFilm.totalsoFar ? '' : currentFilm.totalsoFar
    });
  }, [getFilmById]);
	// console.log(film)
	// console.log(formData)
	let {
		// _id,
		// user,
		// title,
		// date,
		// cinema,
		// image,
		// ticketPrice,
		// crowdfundTarget,
		totalsoFar
	} = formData;

	let tickets = 0;

	const onChange = e => {
		// setFormData({ ...formData, [e.target.name]: e.target.value });
		tickets = currentFilm.totalsoFar + parseInt(e.target.value)
		setBookingTotal(parseInt(currentFilm.ticketPrice) * parseInt(e.target.value));
		setFormData({ ...formData, totalsoFar: tickets });
	}	

	const onSubmit = (e) => {
		e.preventDefault();
		console.log(formData)		
		updateFilm(formData, history);
		//you need to update film in this fn
	};

	return (
	<div>
	<form>
	<div className='checkout-page how-it-works'>
		<div className='checkout-header'>
			<div className='header-block'>
				<span>Film: {currentFilm.title}</span>
			</div>

			<div className='header-block'>
				<span>Date: {currentFilm.date} </span>
			</div>

			<div className='header-block'>
				<span>Price: £{currentFilm.ticketPrice} </span>
			</div>

			<div className='header-block'>
				<span>Crowdfunding Target: {currentFilm.crowdfundTarget} </span>
			</div>
			<div 
			className='header-block'>

			</div>
			<div 
			className='header-block'>
				<span>{currentFilm._id}</span>
			</div>
		</div>
		</div>
		</form>
			<div className='header-block'>
				<input
					type='number'
					placeholder='Enter number of tickets'
					name='tickets'
					onChange={e => onChange(e)}
				/>
			</div>
			<div className='header-block'>
				<span>Basket total: £{bookingTotal}</span>
			</div>
			<div>
			<Link className='btn btn-light my-1' to='/dashboard'>Go Back
			</Link>
			</div>
			<div>

				<span 
				// movie_id={film}
				onClick={e => onSubmit(e)}>
					<StripeCheckoutButton />
					}
				</span>
			</div>
		</div>
	);
};


CheckoutPage.propTypes = {
	updateFilm: PropTypes.func.isRequired,
	// getCurrentProfile: PropTypes.func.isRequired,
	getFilmById: PropTypes.func.isRequired,
	film: PropTypes.object.isRequired,
	profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	profile: state.profile,
	film: state.film
});

export default connect(mapStateToProps, { updateFilm, getFilmById })(
	withRouter(CheckoutPage)
);


