//fix the refresh problem. what is going on? 
import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Film from '../films/Film';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';
import { loadFilms } from '../../actions/film';

const Dashboard = ({ 
  getCurrentProfile, 
  auth: { user }, 
  // profile: { profile, loading },
  films: {film}
  }) => {//starts here
  useEffect(() => {
		getCurrentProfile();
	}, [getCurrentProfile]);

  // if (film === null) return null;
  let arrLength = 5;
  let total = 0;

  return (
        <Fragment>
        <div className="dashboard">
        <h1 className='large text-primary'>Welcome {user && user.name}</h1>
        <h2 className='lead'>
          <i className='fas fa-user' />Your movies 
        </h2>

        </div>  
              <div>
              </div>
              {user !== null ? 
               <Fragment>
               <div>{film.map(item => (
                  item.user._id !== user._id ? total++ :
                  total++ === arrLength ? 
                  <div className="no-profile">
                    <p>You have not yet created a film</p>
                    <Link to='/create-film' className='btn btn-primary my-1'>
                      Create movie
                    </Link>
                  </div> : 
                    <Film
                      key={item._id}
                      film={item}
                    />
              ))}
                <div>
                  <Link to='/create-film' className='btn btn-primary my-1'>
                        Create movie
                  </Link>
                </div> 
              </div>    
              </Fragment> : 
              <Fragment>
               <div className="no-profile">
                    <p>You have not yet created a film</p>
                    <Link to='/create-film' className='btn btn-primary my-1'>
                      Create movie
                    </Link>
                  </div>
              </Fragment> 
              }

        </Fragment>
      )
}

 Dashboard.propTypes = {
	 getCurrentProfile: PropTypes.func.isRequired,
	 //deleteAccount: PropTypes.func.isRequired,

	 auth: PropTypes.object.isRequired,
  loadFilms: PropTypes.func.isRequired,
  films: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
	auth: state.auth,
 films: state.film,
 profile: state.profile
});

export default connect(mapStateToProps, { getCurrentProfile, loadFilms })(Dashboard);          

