import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { loadFilms } from '../../actions/film';
import Film from './Film';

//should you be calling loadfilms here? what does the users do?
//where else woudl you call loadfilms? 
//imports called in actions/film.js   
//in profiles, its called in dashboard.js
//in the place where you want it to appear 
const Films = ({ films }) => {
	//console.log(film)
	useEffect(() => {
    loadFilms();
  }, [loadFilms]);
	console.log(films)
	return (
		<ul>
	        <div className='profiles'>
            {films.length > 0 ? (
              films.map(film => (
                <Film key={film._id} film={film} />
              ))
            ) : (
              <h4>No films found...</h4>
            )}
          </div>	        
	      </ul>
		);
	};
//what are some subtle signs someone gives asking you to do them?
Films.propTypes = {
	//films: PropTypes.object.isRequired,
	loadFilms: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
	films: state.film
});

export default connect(mapStateToProps, { loadFilms })(Films);
//export default connect(mapStateToProps)(Films);

