import React, { useEffect, useState, Fragment } from 'react';
import { Link, withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createFilm, getCurrentProfile, getFilmById } from '../../actions/profile';
import StripeCheckoutButton from '../../components/stripe-button/stripe-button';

const CheckoutPage = ({
	// getFilmById,
	createFilm,
	getCurrentProfile,
	profile: { profile, loading },
	history
}) => {
	const [formData, setFormData] = useState({
		user: '',
		title: '',
		date: '',
		cinema: '',
		image: '',
		ticketPrice: '',
		crowdfundTarget: '',
		totalsoFar: '',
	});

	useEffect(() => {
		getCurrentProfile();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	setFormData({
      user: !profile.user ? '' : profile.user,
      title: !profile.title ? '' : profile.title,
      cinema: !profile.cinema ? '' : profile.cinema,
      date: !profile.date ? '' : profile.date,
      image: !profile.social ? '' : profile.social.image,
      ticketPrice: !profile.social ? '' : profile.social.ticketPrice,
      crowdfundTarget: !profile.social ? '' : profile.social.crowdfundTarget,
      totalsoFar: !profile.social ? '' : profile.social.totalsoFar
    });
  }, [loading, getCurrentProfile]);

	const {
		title,
		date,
		cinema,
		image,
		ticketPrice,
		crowdfundTarget,
		totalsoFar
	} = formData;

	const onChange = e =>
		setFormData({ ...formData, [e.target.name]: e.target.value });

	const onSubmit = e => {
		e.preventDefault();
		createFilm(formData, history, true);
	};

	return (
	<form>
	<div className='checkout-page how-it-works'>
		<div className='checkout-header'>
			<div className='header-block'>
				<span>Film: </span>
			</div>

			<div className='header-block'>
				<span>Date: </span>
			</div>

			<div className='header-block'>
				<input
						type='text'
						placeholder='Enter number of tickets'
						name='tickets'
						onChange={e => onChange(e)}
					/>
			</div>

			<div className='header-block'>
				<span>Price: </span>
			</div>
			<div className='header-block'>
				<span>Crowdfunding Target: </span>
			</div>
			<div className='header-block'>
				<span>Tickets sold: </span>
			</div>
		</div>
		<StripeCheckoutButton />
			<div>
			<Link className='btn btn-light my-1' to='/dashboard'>
						Go Back
			</Link>
			</div>
		</div>
	</form>
	);
};

CheckoutPage.propTypes = {
	createFilm: PropTypes.func.isRequired,
	getCurrentProfile: PropTypes.func.isRequired,
	profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	profile: state.profile
});

export default connect(mapStateToProps, { createFilm, getCurrentProfile })(
	withRouter(CheckoutPage)
);
