import React from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { loadFilms } from '../../actions/film';

const Landing = ({ isAuthenticated }) => {
	if (isAuthenticated) {
    return <Redirect to='/dashboard' />;
  	}
  	// useEffect(() => {
   //  loadFilms();
  	// }, [loadFilms]);

 //next - create a component and import it into landing
	return (
		<section className="landing">
	      <div className="dark-overlay">
	        <div className="landing-inner">
	          <h1 className="x-large">Saturday Cinema Club</h1>
	          <p className="lead">
	            Crowdfunding to watch your favourite movies at the cinema  
	          </p>
	          <div>
	          </div>
	          <div className="buttons">
	            <Link to="/register" className="btn btn-primary">Sign Up
	            </Link>
	            <Link to="/login" className="btn btn-light">Login
	            </Link>
	          </div>
	        </div>
	      </div>
	    </section>
		)
	}

Landing.propTypes = {
  isAuthenticated: PropTypes.bool
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  //films: state.film.films
});

export default connect(mapStateToProps)(Landing);
//export default Landing;
