}

const Film = ({
  film: { title,date,image,cinema,ticketPrice,crowdfundTarget,totalsoFar }
}) => {
//const [ slideIndex, setSlideIndex ] = useState(1)

  return (

    <div>  
        <div className="">
          <img src={image} className="homepage-img fade"/>
            <div>
              <p className="inset-text-title">{title}</p>
              <p className="inset-text-details">{cinema}</p>
              <a className="prev" onClick={showSlides(-1)}>&#10094;</a>
              <a className="next" onClick={showSlides(1)}>&#10094;</a>

            </div>
        </div>
    </div>
  );
};

Film.propTypes = {
  film: PropTypes.object.isRequired
};

export default Film;

