//next get loadfilms to fire on first load. dont wait for click on home screen
//when a user adds a film, reload the page so that it appears immediatley.  

import React, { Fragment, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Register from './components/auth/Register';
import Login from './components/auth/Login'; 
import Navbar from './components/layout/Navbar';
import Landing from './components/layout/Landing';
import Dashboard from './components/dashboard/Dashboard';
import { loadFilms } from './actions/film';
import CreateFilm from './components/profile-forms/CreateFilm';
import Alert from './components/layout/Alert';
import Profile from './components/profile/Profile';
//Redux
import { Provider } from 'react-redux';
import store from './store';
import { loadUser } from './actions/auth';
// import { loadFilms } from '../../actions/film';
import { connect } from 'react-redux';
import setAuthToken from './utils/setAuthToken';
import PrivateRoute from './components/routing/PrivateRoute';
import './App1.css';

if (localStorage.token) {
    setAuthToken(localStorage.token);
  }

const App = () => { 
  useEffect(() => {
    store.dispatch(loadUser())
  }, []);

  useEffect(() =>  {
    	loadFilms();	
	  	}, [loadFilms]);

	return (
	<Provider store={store}>
	   <Router> 
	    <Fragment>
	      	<Navbar />
	      	<Route exact path='/' component={Landing} />
	      	<section className="dark-overlay">
	      		<Alert />
	      		<Switch>
	      		  <Route exact path='/register' component={Register} />
	      		  <Route exact path='/login' component={Login} />
	      		  <Route exact path='/profile/:id' component={Profile} />          
	      		  <PrivateRoute exact path='/dashboard' component={Dashboard} />
	      		  <PrivateRoute exact path='/create-film' component={CreateFilm} />
	      		</Switch>
	      	</section>
	    </Fragment>
	  </Router> 
  	</Provider>
 );
}

Films.propTypes = {
	loadFilms: PropTypes.func.isRequired,
	films: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	films: state.film
});

export default connect(mapStateToProps, { loadFilms })(App);
