  film: { title,date,image,cinema,ticketPrice,crowdfundTarget,totalsoFar }
}) => {

  return (

    <div>  
        <div className="">
          <img src={image} className="homepage-img"/>
            <div>
              <p className="image-text-title">{title}</p>
              <p className="inset-text-details">{cinema}</p>

            </div>
        </div>
    </div>
  );
};

