import React, { useEffect, useState, Fragment } from 'react';
import { useParams} from 'react-router';
import { Link, withRouter, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getCurrentProfile, getFilmById } from '../../actions/profile';
import { updateFilm } from '../../actions/film';
// import { updateFilm } from '../../actions/profile';
import StripeCheckoutButton from '../../components/stripe-button/stripe-button';
import queryString from 'query-string';

const CheckoutPage = ({
	// updateFilm,
	match,
	getCurrentProfile,
	getFilmById,
	film,
	// profile: { profile, loading },
	history
}) => {
	const [formData, setFormData] = useState({
		// _id: '',
		user: '',
		title: '',
		date: '',
		cinema: '',
		image: '',
		ticketPrice: '',
		crowdfundTarget: '',
		totalsoFar: ''
	});

	//const [currentFilm, useParams] = useState(params);

	let params = useParams();
	// console.log(params.title)
	//shouldnt you do this on the server so we're not fetching all? 
	const getFilm = film.film.filter(item => {
		// console.log(item.title)
		return item._id === params._id;
	});
	// remove array brackets
	const currentFilm = getFilm[0]; 
	console.log(currentFilm)
	//set booking total cost for live update on screen
	const [bookingTotal, setBookingTotal] = useState(0);

	// fills the values with current values
	//what does this do, apart from stop setFormData calling infinitely?
	//this fills initial values, and stops repeated calls to setFormData in useState
	//(try it with calling setFomrData in the usestate)  
	useEffect(() => {
		getFilmById(params)
		// getCurrentProfile();
	setFormData({
      _id: !currentFilm._id ? '' : currentFilm._id,
      user: !currentFilm.user ? '' : currentFilm.user,
      title: !currentFilm.title ? '' : currentFilm.title,
      cinema: !currentFilm.cinema ? '' : currentFilm.cinema,
      date: !currentFilm.date ? '' : currentFilm.date,
      image: !currentFilm.image ? '' : currentFilm.image,
      ticketPrice: !currentFilm.ticketPrice ? '' : currentFilm.ticketPrice,
      crowdfundTarget: !currentFilm.crowdfundTarget ? '' : currentFilm.crowdfundTarget,
      totalsoFar: !currentFilm.totalsoFar ? '' : currentFilm.totalsoFar
    });
  }, [getFilmById]);
	// console.log(film)
	// console.log(formData)
	const {
		_id,
		user,
		title,
		date,
		cinema,
		image,
		ticketPrice,
		crowdfundTarget,
		totalsoFar
	} = formData;

	let tickets = 0;

	const onChange = e => {
		// setFormData({ ...formData, [e.target.name]: e.target.value });
		tickets = currentFilm.totalsoFar + parseInt(e.target.value)
		setBookingTotal(parseInt(currentFilm.ticketPrice) * parseInt(e.target.value));
		setFormData({ ...formData, totalsoFar: tickets });
	}	

	const onSubmit = (e) => {
		e.preventDefault();
		//this is fine, it's being submitted here
		//console.log(formData)		
		updateFilm(formData, history);
	};

	return (
	<div>
	<form>
	<div className='checkout-page how-it-works'>
		<div className='checkout-header'>
			<div className='header-block'>
				<span>Film: {currentFilm.title}</span>
			</div>

			<div className='header-block'>
				<span>Date: {currentFilm.date} </span>
			</div>

			<div className='header-block'>
				<span>Price: £{currentFilm.ticketPrice} </span>
			</div>

			<div className='header-block'>
				<span>Crowdfunding Target: {currentFilm.crowdfundTarget} </span>
			</div>
			<div 
			className='header-block'>
				<span>Tickets sold: {currentFilm.totalsoFar}</span>
			</div>
			<div 
			className='header-block'>
				<span>{currentFilm._id}</span>
			</div>
		</div>
		</div>
		</form>
			<div className='header-block'>
				<input
					type='number'
					placeholder='Enter number of tickets'
					name='tickets'
					onChange={e => onChange(e)}
				/>
			</div>
			<div className='header-block'>
				<span>Basket total: £{bookingTotal}</span>
			</div>
			<div>
			<Link className='btn btn-light my-1' to='/dashboard'>Go Back
			</Link>
			</div>
			<div>

				<span 
				// movie_id={film}
				onClick={e => onSubmit(e)}>
					<StripeCheckoutButton />
					}
				</span>
			</div>
		</div>
	);
};


CheckoutPage.propTypes = {
	updateFilm: PropTypes.func.isRequired,
	// getCurrentProfile: PropTypes.func.isRequired,
	getFilmById: PropTypes.func.isRequired,
	film: PropTypes.object.isRequired,
	profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	profile: state.profile,
	film: state.film
});

export default connect(mapStateToProps, { updateFilm, getFilmById })(
	withRouter(CheckoutPage)
);

