const Films = ({ 
	loadFilms,
	films: {film, loading} 
	}) => {

	useEffect(() =>  {
    	loadFilms();	
	  	}, [loadFilms]);

	return (
	<Fragment>
      {film === null || loading ? (
        <Spinner /> 
        ) : (
        <section>
	    	<p>{film}</p>
        </section>    	
		     )
	      }
      </Fragment>
	)
}

Films.propTypes = {
	loadFilms: PropTypes.func.isRequired,
	films: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	films: state.film
});

export default connect(mapStateToProps, { loadFilms })(Films);


