import React from 'react';

const ImageSlide = ({ url, title, cinema, date  }) => {
  const styles = {
    backgroundImage: `url(${url})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center'
  };

  return (
    <div 
    className="image-slide" 
    style={styles}
    title={title}
 	cinema={cinema}
 	date={date}   
	>
		<h2>{title}</h2>
		<h3>{cinema}</h3>
		<h3>{date}</h3>
 	</div>
  );
}

export default ImageSlide;
