const CheckoutPage = ({
	updateFilm,
	match,
	getFilmById,
	films: {film, loading},
	// profile: { profile, loading },
	history
}) => {
	// useEffect(() => {
 //    getFilmById(match.params.id);
 //  }, [getFilmById, match.params.id]);
	// console.log(film)
