import React, { Fragment, useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import { loadFilms } from '../../actions/film';
import Film from './Film';


function showSlides() {
  var slideIndex = 1;  
  var i;
  var slides = document.getElementsByClassName("homepage-img");
  console.log(slides)
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";
  }
  slideIndex++;
  if (slideIndex > slides.length) {slideIndex = 1}
  //console.log(slides)
//why is it calling six times? 
//console.log(slideIndex)
  //slides[slideIndex-1].style.display = "none";
  //slides.style.display = "block";
  setTimeout(showSlides, 2000); // Change image every 2 seconds
}



const Films = ({ 
	loadFilms,
	films: {film, loading} 
	}) => {

	useEffect(() =>  {
    	loadFilms();	
	  	}, [loadFilms]);

	return (
	<Fragment>
      {film === null || loading ? (
        <Spinner /> 
        ) : (
        <section>
	        <div className="slideshow-container">
		           {film.map(item => (
	    				<Film key={item._id} film={item} />
	              	))
		           }
	        </div>

	        <div>
	        	<a className="prev" onClick={showSlides(-1)}>&#10094;</a>


			</div>
        </section>    	
		     )
	      }
      </Fragment>
 	)
 	}

Films.propTypes = {
	loadFilms: PropTypes.func.isRequired,
	films: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	films: state.film
});

export default connect(mapStateToProps, { loadFilms })(Films);

