	      // crowdfundTarget: loading || !film.crowdfundTarget ? '' : film.crowdfundTarget,
	      // totalsoFar: loading || !film.totalsoFar ? '' : film.totalsoFar
	    		});
			//}
	   }, [loading, getFilmById ]);
		// console.log(film)

	//so it's there, it's loaded, so why not showing?
	//what is the relationship of the three elements?

	//creates the vars for the render 
	const {
		_id,
		user,
		title,
		date,
		cinema,
		image,
		ticketPrice,
		crowdfundTarget,
		totalsoFar
	} = formData;

	let tickets = 0;

	//the pattern is this.
	// 1 initiate consts with above		
	// 2 onchange calls setformdata with values attached
	// 3 when is setformdata called, interms of being wrapped in useEffect(fetches current film)?
	// 4 setFormData sets initial state to values.  
	// 5 so state values are either '', or the value attached to film. which is there. but not getting them 
	// 6 are we reloading film. is that the problem? 

	const onChange = e => {
		// setFormData({ ...formData, [e.target.name]: e.target.value });
		tickets = film.totalsoFar + parseInt(e.target.value)
		setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value));
		//setFormData({ ...formData, totalsoFar: tickets });
	}	

	const onSubmit = (e) => {
		e.preventDefault();
		console.log(formData)

		updateFilm(formData, history);
	};

	//if (film === null) return null;
	// useEffect(() => {
	// 	getFilmById(match.params.id)
	// 	// eslint-disable-next-line react-hooks/exhaustive-deps
	// }, [loading, getFilmById]);
	//if (film === null) return null;
	return !film ? (
					<Spinner />	
			   ) : (
			    	<Fragment>  
									<div>
								<form>
								<div className='checkout-page how-it-works'>
									<div className='checkout-header'>
										<div className='header-block'>
											<span>Film: {film.title}</span>
										</div>

										<div className='header-block'>
											<span>Date: {film.date} </span>
										</div>

										<div className='header-block'>
											<span>Price: £{film.ticketPrice} </span>
										</div>

										<div className='header-block'>
											<span>Crowdfunding Target: {film.crowdfundTarget} </span>
										</div>
										<div 
										className='header-block'>
											<span>Tickets sold: {film.totalsoFar}</span>
										</div>
										<div 
										className='header-block'>
											<span>{_id}</span>
										</div>
									</div>
									</div>
									</form>
										<div className='header-block'>
											<input
												type='number'
												placeholder='Enter number of tickets'
												name='tickets'
												onChange={e => onChange(e)}
											/>
										</div>
										<div className='header-block'>
											<span>Basket total: £{bookingTotal}</span>
										</div>
										<div>
										<Link className='btn btn-light my-1' to='/dashboard'>Go Back
										</Link>
										</div>
										<div>
											<span onClick={e => onSubmit(e)}> 
												<StripeCheckoutButton />
											</span>
										</div>
									</div>
					</Fragment>
					// )}
	    // </Fragment>	
		);
	};


CheckoutPage.propTypes = {
	updateFilm: PropTypes.func.isRequired,
	// getCurrentProfile: PropTypes.func.isRequired,
	getFilmById: PropTypes.func.isRequired,
	film: PropTypes.object.isRequired,
	//profile: PropTypes.object.isRequired
