import React, { useEffect, useState, Fragment } from 'react';
import { connect } from 'react-redux';
import { useParams} from 'react-router';
import { useSelector, useDispatch } from 'react-redux';
import { Link, withRouter, Redirect } from 'react-router-dom';
import StripeCheckoutButton from '../../components/stripe-button/stripe-button';
import PropTypes from 'prop-types';
import { getFilmById, updateFilm } from '../../actions/film';
import { updateProfileTickets } from '../../actions/profile';
//import { UPDATE_FILM } from '../../actions/types';
//import './checkout-item.styles.scss';

const CheckoutFilm = ({ 
  film: {film, loading}, 
  match, 
  getFilmById, 
  updateFilm, 
  updateProfileTickets,
  history }) => {

  const [filmData, setFilmData] = useState({
    _id: film._id,
    user: film.user,
    title: film.title,
    date: film.date,
    cinema: film.cinema,
    image: film.image,
    ticketPrice: film.ticketPrice,
    crowdfundTarget: film.crowdfundTarget,
    totalsoFar: film.totalsoFar
  });

  let [bookingTotal, setBookingTotal] = useState(0);

  let tickets = 0;
  let ticketsTotal = film.totalsoFar; 
  let ticketsTotalCost = 5;
  // const onChange = e => {
  //   //ticketsTotal += parseInt(e.target.value)
  //   // setFormData({ ...formData, totalsoFar: ticketsTotal });
  //   // setTicketData({...ticketData, totalsoFar: ticketsTotal })
  //   // // setTicketData({...ticketData, cost: bookingTotal })
  //   // // setTicketData({...ticketData, numberOfTickets: tickets })
  // } 



  //how to sort this mess out? 
  //what do you need to set?
  //when?
//   numberOfTickets first 
  // totalsoFar: film.totalsoFar,
  //   cost: bookingTotal,
  // const onTicketChange = e => {
  //       setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value));
        //ticketsTotal += parseInt(e.target.value)
        // setTicketData({...ticketData, totalsoFar: ticketsTotal })
        //setTicketData({...ticketData, cost: bookingTotal })
        //tickets = e.target.value;
  //}    
  //   setTicketData({...ticketData, totalsoFar: ticketsTotal }),
  //   setTicketData({...ticketData, numberOfTickets: tickets }),


  const onSubmit = (e) => {
    e.preventDefault();
    updateFilm(filmData, history);
    //updateProfileTickets(ticketData, history);
    //console.log(ticketData);
    //console.log(formData);
    //updateProfile(ticketData, history);
  };

  const [ticketData, setTicketData] = useState({
    title: film.title,
    ticketPrice: film.ticketPrice,
    date: film.date,
    cinema: film.cinema,
    crowdfundTarget: film.crowdfundTarget,
    totalsoFar: film.totalsoFar,
    numberOfTickets: tickets,
    cost: '',
  });
  console.log(ticketData)
  console.log(bookingTotal)
  console.log(ticketsTotalCost)


    const changes = (e) => {
    ticketsTotal += parseInt(e.target.value);
    //setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value));
    //tyring to get this to work next
    console.log(ticketsTotalCost)
    setFilmBookingCost(e);
    setTicketBookingCost(ticketsTotalCost)
    // setFilmTicketNumber(e);
    setTicketNumber(e);
    setFilmTotalSoFar(e);
    setTicketTotalSoFar(e);
    // onChange(e);
    // onTicketChange(e);
  }

  const setFilmTotalSoFar = (e) => {
    setFilmData({ ...filmData, totalsoFar: ticketsTotal });
    //setTicketData({...ticketData, totalsoFar: ticketsTotal })
  }
  //works
  const setTicketTotalSoFar = (e) => {
    //setFilmData({ ...filmData, totalsoFar: ticketsTotal });
    setTicketData({...ticketData, totalsoFar: ticketsTotal })
  } 

  const setFilmBookingCost = (e) => {
    //setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value));
    setFilmData({ ...filmData, cost: bookingTotal })
  }
  //nope
  const setTicketBookingCost = (ticketsTotalCost) => {
    //setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value));
    setTicketData({ ...ticketData, cost: ticketsTotalCost })
  }

  const setTicketNumber = (e) => {
    setTicketData({...ticketData, numberOfTickets: parseInt(e.target.value) })
  }

  //   const setFilmTicketNumber = (e) => {
  //   setFilmData({...filmData, numberOfTickets: parseInt(e.target.value) })
  // }




  if (film === null) return null;
  return (
    <div className="film">
    <div>
      <form>
      <div className='checkout-page how-it-works'>
        <div className='checkout-header'>
          <div className='header-block'>

            <span>Film: {film.title}</span>
          </div>

          <div className='header-block'>
            <span>Date: {film.date} </span>
          </div>

          <div className='header-block'>
            <span>Price: £{film.ticketPrice} </span>
          </div>

          <div className='header-block'>
            <span>Crowdfunding Target: {film.crowdfundTarget} </span>
          </div>
          <div 
          className='header-block'>
            <span>Tickets sold: {film.totalsoFar}</span>
          </div>
        </div>
        </div>
        </form>


          <div className='header-block'>
            <input
              type='number'
              placeholder='Enter number of tickets'
              name='tickets'
              onInput={e => setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value))}
              onChange={e => changes(e)}
            />
          </div>
          <div className='header-block'>
            <span>Basket total: £{bookingTotal}</span>
          </div>
          <div>
          <Link className='btn btn-light my-1' to='/dashboard'>Go Back
          </Link>
          </div>
          <div>
            <span onClick={e => onSubmit(e)}> 
              <StripeCheckoutButton />
            </span>
          </div>
        </div>
      </div>
  )
}

CheckoutFilm.propTypes = {
  updateFilm: PropTypes.func.isRequired,
  updateProfileTickets: PropTypes.func.isRequired,
  // getCurrentProfile: PropTypes.func.isRequired,
  getFilmById: PropTypes.func.isRequired,
  //  film: PropTypes.object.isRequired,
  //profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  //profile: state.profile,
  film: state.film
});

export default connect(mapStateToProps, { 
  getFilmById, 
  updateFilm, 
  updateProfileTickets 
})(withRouter(CheckoutFilm));
