import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import ImageSlide from './ImageSlide';
import Arrow from './Arrow'; 
import '../../App1.css';

const Carousel = ({ films: 
	{ film } 
	}) => {

	const [ currentImageIndex, setImageIndex ] = useState();

	//console.log(currentImageIndex)

	useEffect(() => {
    const timer = setTimeout(nextSlide, 1000)
    return () => clearTimeout(timer)
	}, [nextSlide])


// function Loading() {   
     // const [showLoading, setShowLoading] = useState(false)
     // let timer1
     // useEffect(() => {
     //      timer1 = setTimeout(() => setShowLoading(true), 1000)
     //      console.log('running')
     //      // this will clear Timeout when component unmont like in willComponentUnmount
     //      return () => {
     //        clearTimeout(timer1)
     //      }
     //    },[timer1] //useEffect will run only one time
     //       //if you pass a value to array, like this [data] than clearTimeout will run every time this value changes (useEffect re-run)   
     //  )
// }
//works once
// useEffect(() => {
//   const timeout = setTimeout(() => {
//     setImageIndex(currentImageIndex + 1);
//   }, 500);
//   return () => clearTimeout(timeout);
// }, [currentImageIndex]);

	useEffect(() => {
	setImageIndex(0);
	}, [setImageIndex]);

	function previousSlide () {
	console.log('prev')
    const lastIndex = film.length - 1;
    const shouldResetIndex = currentImageIndex === 0;
    const index =  shouldResetIndex ? lastIndex : currentImageIndex - 1;
    setImageIndex(index)	
		}

  	function nextSlide () {
    const lastIndex = film.length - 1;
    const shouldResetIndex = currentImageIndex === lastIndex;
    const index =  shouldResetIndex ? 0 : currentImageIndex + 1;
	setImageIndex(index)
  		}

  		if (film === null) return null;
    return (

      <div className="carousel">

      	<ImageSlide url={ film[currentImageIndex].image } />     

      	<div className="arrows">
      	<Arrow
          direction="left"
          clickFunction={ previousSlide } 
          glyph="&#9664;" 
         />
        <Arrow
          direction="right"
          clickFunction={ nextSlide }
          glyph="&#9654;" 
         />
      	</div>   
      </div>
    );
}

Carousel.propTypes = {
	films: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	films: state.film
});

export default connect(mapStateToProps)(Carousel);
