import React, { Fragment, useEffect } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect, useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
//import Films from '../films/Films';
import Film from '../films/Films';
import { loadFilms } from '../../actions/film';

//get current profile works because it is already loaded.
//next - trace getCurrentProfile, see how it does it onload

const Landing = ({ 
	isAuthenticated, 
	loadFilms, 
	film: {film, loading} }) => {

		// const dispatch = useDispatch();	
		// useEffect(() => { 
  //       dispatch(loadFilms())
  //   }, [dispatch]);


		useEffect(() =>  {
    	loadFilms();	
	  	}, [loadFilms]);
//		console.log(film);

	// if (!film) {
 //      return <div>loading</div>
 //    }

	//fetch call here than set it with useState?

	if (isAuthenticated) {
    return <Redirect to='/dashboard' />;
  	}
 //next - create a component and import it into landing
	return (
		<section className="landing">
	      <div className="dark-overlay">
	        <div className="landing-inner">
		        <div>
				<Fragment>
			      {film === null || loading ? (
			        <Spinner /> 
			        ) : (
			        <Fragment>
		           {film.map(item => (
	    				<Film key={item._id} film={item} />
	              	))
		           }
				</Fragment>    	
		     )
	      }
      </Fragment>
				</div>		
	          <h1 className="x-large">Saturday Cinema Club</h1>
	          <p className="lead">
	            Crowdfunding to watch your favourite movies at the cinema  
	          </p>
	          <div>
	          </div>
	          <div className="buttons">
	            <Link to="/register" className="btn btn-primary">Sign Up
	            </Link>
	            <Link to="/login" className="btn btn-light">Login
	            </Link>
	          </div>
	        </div>
	      </div>
	    </section>
		)
	}

Landing.propTypes = {
  loadFilms: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool,
  film: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  film: state.film
});

export default connect(mapStateToProps, { loadFilms })(Landing);
//export default Landing;
