import React, { useEffect, useState, Fragment } from 'react';
import { connect } from 'react-redux';
import { useParams} from 'react-router';
import { Link, withRouter, Redirect } from 'react-router-dom';
import StripeCheckoutButton from '../../components/stripe-button/stripe-button';
import PropTypes from 'prop-types';
import { getFilmById, updateFilm } from '../../actions/film';
// import { clearItemFromCart, addItem, removeItem } from '../../reducers/cart/cart.actions';
//import './checkout-item.styles.scss';

const CheckoutFilm = ({ film: {film, loading}, match, getFilmById, updateFilm, history }) => {

  const [formData, setFormData] = useState({
    _id: '',
    user: '',
    title: '',
    date: '',
    cinema: '',
    image: '',
    ticketPrice: '',
    crowdfundTarget: '',
    totalsoFar: ''
  });

  console.log(film)

  useEffect(() => { 
        getFilmById(match.params.id); 

  // if(film !== null) {   
  if(!loading && film) {   
  // if(!loading) {
    //console.log('film')
    setFormData({
        // ...state,
        //   totalsoFar: ''
        // _id: film._id,
        // user: film.user,
        // title: film.title,
        // cinema: film.cinema,
        // date: film.date,
        // image: film.image,
        // ticketPrice: film.ticketPrice,
        // crowdfundTarget: film.crowdfundTarget,
        // totalsoFar: film.totalsoFar
        // //you don't actually need this form for the most part. 
        //if(film._id !== '') {_id: film._id }
        _id: loading || !film._id ? '' : film._id,
        user: loading || !film.user ? '' : film.user,
        title: loading || !film.title ? '' : film.title,
        cinema: loading || !film.cinema ? '' : film.cinema,
        date: loading || !film.date ? '' : film.date,
        image: loading || !film.image ? '' : film.image,
        ticketPrice: loading || !film.ticketPrice ? '' : film.ticketPrice,
        crowdfundTarget: loading || !film.crowdfundTarget ? '' : film.crowdfundTarget,
        totalsoFar: loading || !film.totalsoFar ? '' : film.totalsoFar
          });
       }
     }, [loading, getFilmById]);


  const { _id, title,date,cinema,image,ticketPrice,crowdfundTarget,totalsoFar } = formData;
  //console.log(film)

  // const [formData, setFormData] = useState({
  //   totalsoFar: ''
  // });

  let [bookingTotal, setBookingTotal] = useState(0);
  let tickets = 0;


  const onChange = e => {
    //setFormData({ ...formData, [e.target.name]: e.target.value });
    tickets = film.totalsoFar + parseInt(e.target.value)
    setBookingTotal(parseInt(film.ticketPrice) * parseInt(e.target.value));
    setFormData({ ...formData, totalsoFar: tickets });
  } 

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(formData)
    updateFilm(formData, history);
  };


  if (film === null) return null;
  return (
    <div className="film">


                    <div>
                <form>
                <div className='checkout-page how-it-works'>
                  <div className='checkout-header'>
                    <div className='header-block'>
                      <span>Film: {film.title}</span>
                    </div>

                    <div className='header-block'>
                      <span>Date: {film.date} </span>
                    </div>

                    <div className='header-block'>
                      <span>Price: £{film.ticketPrice} </span>
                    </div>

                    <div className='header-block'>
                      <span>Crowdfunding Target: {film.crowdfundTarget} </span>
                    </div>
                    <div 
                    className='header-block'>
                      <span>Tickets sold: {film.totalsoFar}</span>
                    </div>
                  </div>
                  </div>
                  </form>


                    <div className='header-block'>
                      <input
                        type='number'
                        placeholder='Enter number of tickets'
                        name='tickets'
                        onChange={e => onChange(e)}
                      />
                    </div>
                    <div className='header-block'>
                      <span>Basket total: £{bookingTotal}</span>
                    </div>
                    <div>
                    <Link className='btn btn-light my-1' to='/dashboard'>Go Back
                    </Link>
                    </div>
                    <div>
                      <span onClick={e => onSubmit(e)}> 
                        <StripeCheckoutButton />
                      </span>
                    </div>
                  </div>


    </div>
  )
}

CheckoutFilm.propTypes = {
  updateFilm: PropTypes.func.isRequired,
  // getCurrentProfile: PropTypes.func.isRequired,
  getFilmById: PropTypes.func.isRequired,
  //  film: PropTypes.object.isRequired,
  //profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  //profile: state.profile,
  film: state.film
});


// const mapDispatchToProps = dispatch => ({

//   })

export default connect(mapStateToProps, { getFilmById, updateFilm })(
  withRouter(CheckoutFilm)
);
