const Films = ({ 
	loadFilms, 
	films: {film, loading} 
	}) => {

	useEffect(() => {
    loadFilms();
  	}, [loadFilms]);
	//console.log(film)
	return film === null || loading ? <Spinner /> :
     (
        <Fragment>
    		<ul>
	         <div className='profiles'>
