import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Films from '../films/Films';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';

const Dashboard = ({ getCurrentProfile, 
  auth: { user }, 
  profile: { profile, loading } }) => {
	useEffect(() => {
		getCurrentProfile();
	}, [getCurrentProfile]);

	return loading && profile === null ? <Spinner /> : 
      <Fragment>
      <h1 className='large text-primary'>Dashboard</h1>
      <p className='lead'>
        <i className='fas fa-user' /> Welcome {user && user.name}
      </p>
      {profile !== null ? (
        <Fragment>
          //next - emulate profile in devconnect
          //you have to build in the films component here
          <Films/>
        <p>{console.log(profile)}</p>
          <p>{console.log(profile.title)}</p>
          <p>{console.log(profile.location)}</p>
          <p>{console.log(profile.date)}</p>
          <p>{profile.title}</p>
        </Fragment>
      ) : (
        <Fragment>
          <div className='form-group'>
          <p>You have not yet setup a profile, please add some info</p>
          <p>{console.log('not working')}</p>
          <Link to='/create-profile' className='btn btn-primary my-1'>
            Create Profile
          </Link>
          </div>
        </Fragment>
      )}

    </Fragment>
};


Dashboard.propTypes = {
	getCurrentProfile: PropTypes.func.isRequired,
	//deleteAccount: PropTypes.func.isRequired,
	auth: PropTypes.object.isRequired,
	profile: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
	auth: state.auth,
	profile: state.profile
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);





