import {
  GET_FILM,
  GET_FILMS,
  PROFILE_ERROR,
  UPDATE_FILM,
  CLEAR_PROFILE,
  ACCOUNT_DELETED,
  GET_REPOS
} from '../actions/types';

const initialState = {
  film: null,
  films: [],
  loading: true,
  error: {}
};

export default function(state = initialState, action) {
         const { type, payload } = action 
         console.log(payload);
        switch (type) {
          case GET_FILM:
          case GET_FILMS:
          case UPDATE_FILM:
          return {
            ...state,
            film: payload,
            loading: false 
          }; 
          case PROFILE_ERROR:
          return {
            ...state,
            error: payload,
            loading: false
          }; 
          case CLEAR_PROFILE:
          return {
            ...state,
            film: null,

            loading: false 
          }; 
          default:
            return state
        }
    }
