import React, { Fragment, useLayoutEffect, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import { loadFilms } from '../../actions/film';
import Film from './Film';

const Films = ({ 
	loadFilms, 
	films: {film, loading} 
	}) => {

	useEffect(() => {
		const [setState] = useState('');
		const fetchData = async () => {
			const data = await loadFilms();
			setState(data)
			}
			fetchData()
			}, []); 


	// useEffect(async () => {
 //    loadFilms();
 //  	}, [loadFilms]);
	//console.log(film)	

// loading ? <Spinner />
	return film === null ? (null) :
     (
        <Fragment>
    		<ul>
	           <div>{film.map(film => (
    				<Film key={film._id} film={film} />
              	))}
              </div>	
          	</ul>
        </Fragment>    	
      )
 	};

Films.propTypes = {
	loadFilms: PropTypes.func.isRequired,
	films: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	films: state.film
});

export default connect(mapStateToProps, { loadFilms })(Films);
