import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { loadFilms } from '../../actions/film';
import Film from './Film';

//should you be calling loadfilms here? what does the users do?
//where else woudl you call loadfilms? 
//imports called in actions/film.js   
//in profiles, its called in dashboard.js
//in the place where you want it to appear 
const Films = ({ loadFilms, films: {film, loading} }) => {

	useEffect(() => {
    loadFilms();
  	}, [loadFilms]);

	return (
    <Fragment>
      {film === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
    		<ul>
	         <div className='profiles'>
	            <h1>LOL</h1>
	            { film }
	         </div>
	    	</ul>      

          <div className='profile-grid my-1'>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};


