// todo 
//at the moent - trying to get the screen to update with new model
// 1 enter number of tickets, calculate price,  go to booking payment
// 2 send new object to createFilm
// 3 create tickets, return to dashboard with tickets on dashboard
// 4 with two sections, one for films, one for tickets 

import React, { useEffect, useState, Fragment } from 'react';
import { Link, withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createFilm, getCurrentProfile } from '../../actions/profile';
import StripeCheckoutButton from '../../components/stripe-button/stripe-button';

const CheckoutPage = ({
	createFilm,
	// getCurrentProfile,
	profile: { profile, loading },
	history
}) => {

	const [formData, setFormData] = useState({
		user: '',
		title: '',
		date: '',
		cinema: '',
		image: '',
		ticketPrice: '',
		crowdfundTarget: '',
		totalsoFar: ''
	});

	useEffect(() => {
		getCurrentProfile();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	setFormData({
      user: !profile.user ? '' : profile.user,
      title: !profile.title ? '' : profile.title,
      cinema: !profile.cinema ? '' : profile.cinema,
      date: !profile.date ? '' : profile.date,
      image: !profile.image ? '' : profile.image,
      ticketPrice: !profile.ticketPrice ? '' : profile.ticketPrice,
      crowdfundTarget: !profile.crowdfundTarget ? '' : profile.crowdfundTarget,
      totalsoFar: !newTicketsTotal ? '' : newTicketsTotal
    });
  }, [loading, getCurrentProfile]);
	//console.log(profile)
	const {
		title,
		date,
		cinema,
		image,
		ticketPrice,
		crowdfundTarget,
		totalsoFar
	} = formData;


	let tickets = 0;
	let bookingTotal = 0
	//let bookingTotal = 0;

	const newTicketsTotal = (tickets) => {
			let newTotal = parseInt(profile.totalsoFar) + parseInt(tickets);
			bookingTotal = parseInt(profile.ticketPrice) * parseInt(tickets);
			// console.log(bookingTotal)	
			return newTotal; 
	}

	// const onTicketChange = e => {
	// 	tickets = e.target.value
	// 	// console.log(tickets)
	// 	// newTicketsTotal += tickets;
	// 	// bookingTotal = profile.ticketPrice * tickets;

	// 	let totalsoFar = newTicketsTotal(tickets)
	// 	// console.log(tickets)
	// 	console.log(totalsoFar)
	// 	// console.log(bookingTotal)
	// }

	const onChange = e => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
		tickets = e.target.value
		// console.log(tickets)
		// newTicketsTotal += tickets;
		// bookingTotal = profile.ticketPrice * tickets;
		profile.totalsoFar = newTicketsTotal(tickets) 
		// let bookedTickets = newTicketsTotal(tickets)
		// console.log(tickets)
		// console.log(bookedTickets)	
	}


	const onSubmit = e => {
		e.preventDefault();
		console.log(formData)
		createFilm(formData, history, true);
		// console.log(newTicketsTotal);
		// console.log(bookingTotal);
	};

	return (
	<div>
	<form>
	<div className='checkout-page how-it-works'>
		<div className='checkout-header'>
			<div className='header-block'>
				<span>Film: {profile.title}</span>
			</div>

			<div className='header-block'>
				<span>Date: {profile.date} </span>
			</div>

			<div className='header-block'>
				<span>Price: {profile.ticketPrice} </span>
			</div>

			<div className='header-block'>
				<span>Crowdfunding Target: {profile.crowdfundTarget} </span>
			</div>

			<div 
			className='header-block'
			value={totalsoFar}>
				<span>Tickets sold: {totalsoFar}</span>
			</div>
		</div>
		</div>
		</form>
			<div className='header-block'>
				<input
						type='number'
						placeholder='Enter number of tickets'
						name='tickets'
						onChange={e => onChange(e)}
					/>
			</div>
			<div className='header-block'>
				<span>Basket total: Â£{bookingTotal.toFixed(2)}</span>
			</div>
			<div>
			<Link className='btn btn-light my-1' to='/dashboard'>
						Go Back
			</Link>
			</div>
			<div>
			<StripeCheckoutButton onSubmit={e => onSubmit(e)}/>
			</div>
		</div>
	);
};

CheckoutPage.propTypes = {
	createFilm: PropTypes.func.isRequired,
	getCurrentProfile: PropTypes.func.isRequired,
	profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	profile: state.profile
});

export default connect(mapStateToProps, { createFilm, getCurrentProfile })(
	withRouter(CheckoutPage)
);
