import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Film from '../films/Film';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';
import Ticket from '../ticket/Ticket';
import { createProfile } from '../../actions/profile';

const Dashboard = ({ 
  auth: { user, loading }, 
  film: { films },
  history
  }) => {
// console.log(user)
// console.log(films)
return 
  loading && user === null ? <Spinner /> :   
        <Fragment>
        <div className="dashboard">
        <h1 className='large text-primary'>Welcome {user && user.name}</h1>
        <h2 className='lead'>
          <i className='fas fa-user' />Your movies and tickets 
        </h2>

        </div>  

        {user.tickets !== null ? (   
        <Fragment>
        <h1 className="displayHeader">Your tickets</h1>
        <div className="displayDash">{user.tickets.map(item => (         
             <div 
             className="singleTicket"
             key={item._id}>
             <Ticket
               ticket={item}
             />
             </div> 
        ))}
        </div> 
        </Fragment>
         ) : (
         <Fragment>  
              <p>No tickets</p>
          </Fragment>
          )
        }
      </Fragment>

}

 Dashboard.propTypes = {
	  getCurrentProfile: PropTypes.func.isRequired,
	 //deleteAccount: PropTypes.func.isRequired,
	  auth: PropTypes.object.isRequired,
    // loadFilms: PropTypes.func.isRequired,
    film: PropTypes.object.isRequired,
    profiles: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
 auth: state.auth,
 film: state.film,
 profiles: state.profile
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);                              


