import React, { useEffect, useState, Fragment } from 'react';
import { connect } from 'react-redux';
import { useParams} from 'react-router';
import { Link, withRouter, Redirect } from 'react-router-dom';
import StripeCheckoutButton from '../../components/stripe-button/stripe-button';
import PropTypes from 'prop-types';
import { updateFilm } from '../../actions/film';
// import { clearItemFromCart, addItem, removeItem } from '../../reducers/cart/cart.actions';
//import './checkout-item.styles.scss';

const CheckoutFilm = ({ film }) => {
  const { _id, title,date,cinema,image,ticketPrice,crowdfundTarget,totalsoFar } = film;
  //console.log(film)
  console.log(_id)
  // const [formData, setFormData] = useState({
  //   _id: film._id,
