import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import { loadFilms } from '../../actions/film';
import Film from './Film';


const Films = ({ 
	loadFilms,
	films: {film, loading} 
	}) => {

	const [allFilms, setAllFilms] = useState();

	useEffect(() => {
    axios
      .get("http://localhost:5000/api/film")
