import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Film from '../films/Film';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';
// import { loadFilms } from '../../actions/film';

//copy from dev
//profile should load on login

const Dashboard = ({ 
  getCurrentProfile, 
  auth: { user }, 
  profile: { profile, loading },
  film: {films}
  }) => {
  useEffect(() => {
		//of course, get currentprofile is a film
    getCurrentProfile();
	}, [getCurrentProfile]);
  //where are you valling filmbyID

   console.log(profile)
  return loading && profile === null ? <Spinner /> : 
  // return (
        <Fragment>
        <div className="dashboard">
        <h1 className='large text-primary'>Welcome {user && user.name}</h1>
        <h2 className='lead'>
          <i className='fas fa-user' />Your movies and tickets 
        </h2>

        </div>  
            {profile !== null ? 

               <Fragment>
               <div>{films.map(item => (

                  //test if user has any films 
                  //console.log(item.user.id)
                  //get this to work next 

                  //build it on here
                    item.user === user._id ?

                    <div key={item._id}>
                    <Film
                      film={item}

                    />
                    </div> : null



              //so here, tickets,
              //it's costly, in terms of memory
              //should be listed in user, but could use that as an interview point  

              ))}
                <div>
                  <Link to='/create-film' className='btn btn-primary my-1'>
                        Create movie
                  </Link>
                </div> 
              </div>    
              </Fragment> :   
              <Fragment>
               <div className="no-profile">
                    <p>You have not yet created a film</p>
                    <Link to='/create-film' className='btn btn-primary my-1'>
                      Create movie
                    </Link>
                  </div>
              </Fragment> 
              }  
        </Fragment>
     // )
}

 Dashboard.propTypes = {
	  getCurrentProfile: PropTypes.func.isRequired,
	 //deleteAccount: PropTypes.func.isRequired,
	  auth: PropTypes.object.isRequired,
    // loadFilms: PropTypes.func.isRequired,
    film: PropTypes.object.isRequired,
    // profile: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
 auth: state.auth,
 film: state.film,
 // profile: state.profile
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);          


