import React, { Fragment, useEffect } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect, useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import Films from '../films/Films';
//import Film from '../films/Films';
import { loadFilms } from '../../actions/film';

const Landing = ({ isAuthenticated }) => {

	if (isAuthenticated) {
    return <Redirect to='/dashboard' />;
  	}

	return (
		<section className="landing">
	      <div className="dark-overlay">
		       <div className="filmscroller"> 
		        	<Films/>
		       </div>
	        <div className="landing-page-titles">
	          <h1 className="x-large">Saturday Cinema Club</h1>
	          <p className="lead">
	            Crowdfunding to watch your favourite movies at the cinema  
	          </p>

	          <div>

	          </div>
	          <div className="buttons">
	            <Link to="/register" className="btn btn-primary">Sign Up
	            </Link>
	            <Link to="/login" className="btn btn-light">Login
	            </Link>
	          </div>
	        </div>
	      </div>
	    </section>
		)
	}

Landing.propTypes = {
  loadFilms: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool,
  film: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  film: state.film
});

export default connect(mapStateToProps, { loadFilms })(Landing);
//export default Landing;
