import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Film from '../films/Film';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';
// import { loadFilms } from '../../actions/film';

//copy from dev
//profile should load on login

const Dashboard = ({ 
  getCurrentProfile, 
  auth: { user }, 
  profile: { profile, loading, tickets },
  film: {films}
  }) => {
  useEffect(() => {
	  getCurrentProfile();
	}, [getCurrentProfile]);
  //where are you calling filmbyID????

   // console.log(films)
   // console.log(user)
  //return user === null ? <Spinner /> : (
    // return loading && user === null ? <Spinner /> : (
      return (
        <Fragment>
        <div className="dashboard">
        <h1 className='large text-primary'>Welcome {user && user.name}</h1>
        <h2 className='lead'>
          <i className='fas fa-user' />Your movies and tickets 
        </h2>

        </div>  
               <Fragment>
               <div>{films.map(item => (
                    item.user === user._id ?
                    <div key={item._id}>
                    <Film
                      film={item}
                    />
                    </div> : null
              ))}
               </div> 
               </Fragment>


              <Fragment>
              <div>{tickets.map(item => (
                    <div>
                    <p>tickets</p>
                    </div>
                    ))} 
                <div>
                  <Link to='/' className='btn btn-primary my-1'>
                        <p>You have not bought any tickets. Click to search films</p>  
                  </Link>
                </div> 
              </div>    
          </Fragment>
          </Fragment>

     )
}

 Dashboard.propTypes = {
	  getCurrentProfile: PropTypes.func.isRequired,
	 //deleteAccount: PropTypes.func.isRequired,
	  auth: PropTypes.object.isRequired,
    // loadFilms: PropTypes.func.isRequired,
    film: PropTypes.object.isRequired,
    profile: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
 auth: state.auth,
 film: state.film,
 profile: state.profile
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);                              
