import React, { useEffect } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Films from '../films/Films';
import { loadFilms } from '../../actions/film';

const Landing = ({ isAuthenticated, loadFilms, film }) => {

	useEffect(() => {
    loadFilms();
  	}, [loadFilms]);
	console.log(film)

	if (isAuthenticated) {
    return <Redirect to='/dashboard' />;
  	}
 //next - create a component and import it into landing
	return (
		<section className="landing">
		<div>
		<Films/>	          
		</div>		
	      <div className="dark-overlay">
	        <div className="landing-inner">
	          <h1 className="x-large">Saturday Cinema Club</h1>
	          <p className="lead">
	            Crowdfunding to watch your favourite movies at the cinema  
	          </p>
	          <div>
	          </div>
	          <div className="buttons">
	            <Link to="/register" className="btn btn-primary">Sign Up
	            </Link>
	            <Link to="/login" className="btn btn-light">Login
	            </Link>
	          </div>
	        </div>
	      </div>
	    </section>
		)
	}

Landing.propTypes = {
  loadFilms: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool,
  film: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  film: state.film
});

export default connect(mapStateToProps, { loadFilms })(Landing);
//export default Landing;
