import React, { Fragment, useState } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/auth';
import menu from '../howitworks/menu.png'
import '../../App.scss';

const Navbar = ({ auth: { isAuthenticated, loading }, logout }) => {

	const [ dropdownActive, setDropdownActive ] = useState(false);
	// console.log(is);
	 // className="navbar-auth"
	 // className="navbar-right"
	const authLinks = (
		<ul className="loginlinks navbar-right">
	        <li>
		        <Link to='/'>
		        <i />{' '}
		        <span>Film showings</span>
		        </Link>
	        </li>
	        <li>
		        <Link to='/film/dashboard'>
		        <i/>{' '}
		        <span>Your films and tickets</span>
		        </Link>
	        </li>
	        <li onClick={logout}>

		        <Link to='/'>
		        <i/>{' '}
		        <span>Logout</span>
		        </Link>

	        </li>
	        <li >
			        <Link to='/howitworks'>
			        How it works
			        </Link>
		        </li>        
	      <div 
		      className="hamburgerDiv"
		      // onClick={setActive(true)}
		      >
		        <p className="hamburger"></p>
				<p className="hamburger"></p>
				<p className="hamburger"></p>
		  	</div> 
	      </ul>

		);

	const guestLinks = (

		<ul className="navbar-right">
	        <div className='auth'>
	        <li className={`${dropdownActive ? "reg-dropdown" : "register"}`}>
		        <Link to="/register">Register</Link>
	        </li>
	        <li className='login'>
	    	    <Link  to="/login"> Login</Link>
	        </li>
	        </div>
	        <li className='howitworks' >
		        <Link to='/howitworks'>
		        How it works
		        </Link>
	        </li>
		 <div 
		      className="hamburgerDiv"
		      onClick={() => setDropdownActive(!dropdownActive)}
		      >
		        <p className="hamburger"></p>
				<p className="hamburger"></p>
				<p className="hamburger"></p>
		  	</div> 	
		  	<div className={`${dropdownActive ? "dropdown" : ""}`}>
	      	</div>
	      </ul>


		);

	//next, make it fold
	//then a drop down
	//why doesn't it work on mobile? 
	return (

		<nav className="navbar">
	      <div className="navbar-left">
			<Link className="main-title" to="/">
	        	<h1>Saturday Cinema Club</h1> 
	        </Link>

	      <h2 className="main-title-subdeck">
	            Your favourite films on the big screen  
	      </h2>
 	      </div>

	      {  (
	      	<Fragment>{ isAuthenticated ? authLinks : guestLinks }</Fragment>
	      	)}

	    </nav>
		);
	};

Navbar.propTypes = {
	logout: PropTypes.func.isRequired,
	auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	auth: state.auth
});

export default connect(mapStateToProps, { logout })(Navbar);


