import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import ImageSlide from './ImageSlide';
import Arrow from './Arrow'; 
import BR from './BR.jpeg';
import Love from './love.jpeg';
import Tap from './tap.jpeg';
import '../../App.scss';


const filmsArr = ['BR', 'Love', 'Tap']


const Carousel = ({ 
  film: { films } 
  }) => {
  //console.log(films)

  const [ currentImageIndex, setImageIndex ] = useState([0]);

  const [active, setActive] = useState();

  useEffect(() => {
    const timer = setTimeout(nextSlide, 3000);
    return () => clearTimeout(timer);
  }, [nextSlide])

  useEffect(() => {
  setImageIndex(0);
  }, [setImageIndex]);

  function previousSlide () {
    const lastIndex = filmsArr.length - 1;
    // const lastIndex = films.length - 1;
    const shouldResetIndex = currentImageIndex === 0;
    const index = shouldResetIndex ? lastIndex : currentImageIndex - 1;
    setImageIndex(index)  
  }

  function nextSlide () {
    const lastIndex = filmsArr.length - 1;
    // const lastIndex = films.length - 1;
    const shouldResetIndex = currentImageIndex === lastIndex;
    const index = shouldResetIndex ? 0 : currentImageIndex + 1;
  setImageIndex(index)
    }

  if (films === null) return null;
    return (

      <>
        <ImageSlide 

        // className={active ? 'fade image-slide' : 'image-slide'} 
        url={ filmsArr[currentImageIndex].image } 
        title={ filmsArr[currentImageIndex].title }
        cinema={ filmsArr[currentImageIndex].cinema }
        date={ filmsArr[currentImageIndex].date }
        />     
        <div className="arrows">
        <Arrow
          direction="prev"
          clickFunction={ previousSlide } 
          glyph="&#9664;" 
         />
        <Arrow
          direction="next"
          clickFunction={ nextSlide }
          glyph="&#9654;" 
         />
        </div>  
        <img src={BR} />
      </> 
    );
}

Carousel.propTypes = {
  film: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  film: state.film
});

export default connect(mapStateToProps)(Carousel);

