import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Film from '../films/Film';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';

const Dashboard = ({ 
  getCurrentProfile, 
  auth: { user }, 
  films: {film, loading}, 
  profile: { profile } 
  }) => {//starts here
	useEffect(() => {
		getCurrentProfile();
	}, [getCurrentProfile]);
  //use localstatestorage here? 
	if (film === null) return null;
  const arrLength = film.length;
  const total = 0;
  return (
        <Fragment>
        <div>
        <h1 className='large text-primary'>Your movies</h1>
        <p className='lead'>
          <i className='fas fa-user' /> Welcome {user && user.name}
        </p>
        </div>
              <div>{film.map(item => (
                  item.user_id !== user.id ? total++ :
                  total++ === arrLength ? 
                  <div className="no-profile">
                    <p>You have not yet created a film</p>
                    <Link to='/create-film' className='btn btn-primary my-1'>
                      Create movie
                    </Link>
                  </div>    
                  : 
                  <div className="film">
                    <Film
                      film={item}
                      key={item._id}
                    />
                  </div> 

              ))}
              </div>    
        </Fragment>
      );
    };

 Dashboard.propTypes = {
	getCurrentProfile: PropTypes.func.isRequired,
	//deleteAccount: PropTypes.func.isRequired,
	auth: PropTypes.object.isRequired,
	profile: PropTypes.object.isRequired,
  films: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
	auth: state.auth,
	profile: state.profile,
  films: state.film
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);          
