import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/auth';

const Navbar = ({ auth: { isAuthenticated, loading }, logout }) => {
	// console.log(is);
	 // className="navbar-auth"
	 // className="navbar-right"
	const authLinks = (
		<ul>
	        <li>
		        <Link to='/'>
		        <i />{' '}
		        <span>Film showings</span>
		        </Link>
	        </li>
	        <li>
		        <Link to='/film/dashboard'>
		        <i/>{' '}
		        <span>Your films and tickets</span>
		        </Link>
	        </li>
	        <li onClick={logout}>

		        <Link to='/'>
		        <i/>{' '}
		        <span>Logout</span>
		        </Link>

	        </li>
	        <li >
			        <Link to='/howitworks'>
			        How it works
			        </Link>
		        </li>        
	      </ul>
		);

	const guestLinks = (
		// className="navbar-auth"
		//className="navbar-right"
		<ul className="loginlinks">
	        <li className='register'>
		        <Link to="/register">Register</Link>
	        </li>
	        <li className='login'>
	    	    <Link  to="/login">Login</Link>
	        </li>

		        <li className='guide-link' >
			        <Link to='/howitworks'>
			        How it works
			        </Link>
		        </li>
	      </ul>
		);

	return (

		<nav className="navbar">
	      <div >
			<Link to="/">
	        	<h1 className="main-title">Saturday Cinema Club</h1> 
	        </Link>

	      <h2 className="main-title-subdeck">
	            Your favourite films on the big screen  
	      </h2>
 	      </div>

	      {  (
	      	<Fragment>{ isAuthenticated ? authLinks : guestLinks }</Fragment>
	      	)}
	    </nav>
		);
	};

Navbar.propTypes = {
	logout: PropTypes.func.isRequired,
	auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	auth: state.auth
});

export default connect(mapStateToProps, { logout })(Navbar);


