import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Film from '../films/Film';
import PropTypes from 'prop-types';
import Spinner from '../layout/spinner';
import { connect } from 'react-redux';
import DashboardActions from './DashboardActions';
import { getCurrentProfile } from '../../actions/profile';
// import { loadFilms } from '../../actions/film';

//remember, profle IS film
//ok, trying to get each user's films to appear on their dashboard
// api/profile/me not working

//once they appear correctly, rejig the updatefilm to appear in profile
//with the createfilm fn ie GET PROL 


const Dashboard = ({ 
  getCurrentProfile, 
  auth: { user }, 
  profile: { profile, loading },
  films: {film}
  }) => {//starts here
  useEffect(() => {
		//of course, get currentprofile is a film
    getCurrentProfile();
	}, [getCurrentProfile]);
   //if (film === null) return null;

  let arrLength = 5;
  let total = 0;
  console.log(user.id)
  console.log(film.id)
  return (
        <Fragment>
        <div className="dashboard">
        <h1 className='large text-primary'>Welcome {user && user.name}</h1>
        <h2 className='lead'>
          <i className='fas fa-user' />Your movies 
        </h2>

        </div>  
            {user !== null ? 

               <Fragment>
               <div>{film.map(item => (
                  //test if user has any films 
                  //console.log(item.user.id)

                  item.user.id !== user.id ? total++ :
                  total++ === arrLength ? 
                  <div className="no-profile">
                    <p>You have not yet created a film</p>
                    <Link to='/create-film' className='btn btn-primary my-1'>
                      Create movie
                    </Link>
                  </div> : 
                    item.user.id === user.id ?                  
                    <div key={item._id}>
                    <Film
                      film={item}
                    />
                    </div> : null
              ))}
                <div>
                  <Link to='/create-film' className='btn btn-primary my-1'>
                        Create movie
                  </Link>
                </div> 
              </div>    
              </Fragment> :   
              <Fragment>
               <div className="no-profile">
                    <p>You have not yet created a film</p>
                    <Link to='/create-film' className='btn btn-primary my-1'>
                      Create movie
                    </Link>
                  </div>
              </Fragment> 
              }  
        </Fragment>
      )
}

 Dashboard.propTypes = {
	  getCurrentProfile: PropTypes.func.isRequired,
	 //deleteAccount: PropTypes.func.isRequired,
	  auth: PropTypes.object.isRequired,
    // loadFilms: PropTypes.func.isRequired,
    films: PropTypes.object.isRequired,
    profile: PropTypes.object.isRequired
};  

const mapStateToProps = state => ({
 auth: state.auth,
 films: state.film,
 profile: state.profile
});

export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);          

